mapper.select:

            select * 
            from (
            select rownum rn, cartId, memberId, itemId, itemCount
            from (select * from tbl_cart order by



		             memberId 


		             itemId 


		        	 cartId 




	        		 asc)
	            where 



            rownum <=  #{pageNum} * #{amount}
            ) where rn > (#{pageNum}-1) * #{amount}


            select * 
            from (
            select rownum rn, cartId, memberId, itemId, itemCount
            from (select * from tbl_cart order by 


		             memberId 


		             itemId 


		        	 cartId 



		     desc)
	            where 



            rownum <=  #{pageNum} * #{amount}
            ) where rn > (#{pageNum}-1) * #{amount}
