mapper.update:
update tbl_faq 
set faqName=#{faqName}, faqDescription=#{faqDescription}, faqPrice=#{faqPrice}, faqCategory=#{faqCategory}, faqBrand=#{faqBrand}, faqImageURL=#{faqImageURL}
where faqId=#{faqId}

mapper.select:

select faqId, faqName, faqDescription, faqPrice, faqCategory, faqBrand, faqImageURL, faqRegdate, faqUpdateDate from (
select /*+INDEX_DESC(tbl_faq pk_faq) */ rownum rn, faqId, faqName, faqDescription, faqPrice, faqCategory, faqBrand, faqImageURL, faqRegdate, faqUpdateDate
from tbl_faq 
where rownum <=  #{pageNum} * #{amount}
) where rn > (#{pageNum}-1) * #{amount}
