mapper.update:
update tbl_member 
set memberName=#{memberName}, memberDescription=#{memberDescription}, memberPrice=#{memberPrice}, memberCategory=#{memberCategory}, memberBrand=#{memberBrand}, memberImageURL=#{memberImageURL}
where memberId=#{memberId}

mapper.select:

select memberId, memberName, memberDescription, memberPrice, memberCategory, memberBrand, memberImageURL, memberRegdate, memberUpdateDate from (
select /*+INDEX_DESC(tbl_member pk_member) */ rownum rn, memberId, memberName, memberDescription, memberPrice, memberCategory, memberBrand, memberImageURL, memberRegdate, memberUpdateDate
from tbl_member 
where rownum <=  #{pageNum} * #{amount}
) where rn > (#{pageNum}-1) * #{amount}
