mapper.update:
update tbl_notice 
set noticeName=#{noticeName}, noticeDescription=#{noticeDescription}, noticePrice=#{noticePrice}, noticeCategory=#{noticeCategory}, noticeBrand=#{noticeBrand}, noticeImageURL=#{noticeImageURL}
where noticeId=#{noticeId}

mapper.select:

select noticeId, noticeName, noticeDescription, noticePrice, noticeCategory, noticeBrand, noticeImageURL, noticeRegdate, noticeUpdateDate from (
select /*+INDEX_DESC(tbl_notice pk_notice) */ rownum rn, noticeId, noticeName, noticeDescription, noticePrice, noticeCategory, noticeBrand, noticeImageURL, noticeRegdate, noticeUpdateDate
from tbl_notice 
where rownum <=  #{pageNum} * #{amount}
) where rn > (#{pageNum}-1) * #{amount}
