mapper.update:
update tbl_qna 
set qnaName=#{qnaName}, qnaDescription=#{qnaDescription}, qnaPrice=#{qnaPrice}, qnaCategory=#{qnaCategory}, qnaBrand=#{qnaBrand}, qnaImageURL=#{qnaImageURL}
where qnaId=#{qnaId}

mapper.select:

select qnaId, qnaName, qnaDescription, qnaPrice, qnaCategory, qnaBrand, qnaImageURL, qnaRegdate, qnaUpdateDate
from (
	select /*+INDEX_DESC(tbl_qna pk_qna) */ rownum rn, qnaId, qnaName, qnaDescription, qnaPrice, qnaCategory, qnaBrand, qnaImageURL, qnaRegdate, qnaUpdateDate
from tbl_qna 
where 





rownum <=  #{pageNum} * #{amount}
) where rn > (#{pageNum}-1) * #{amount}


select qnaId, qnaName, qnaDescription, qnaPrice, qnaCategory, qnaBrand, qnaImageURL, qnaRegdate, qnaUpdateDate
from (
    select /*+INDEX_DESC(tbl_qna pk_qna) */ rownum rn, qnaId, qnaName, qnaDescription, qnaPrice, qnaCategory, qnaBrand, qnaImageURL, qnaRegdate, qnaUpdateDate
from (
        select * from tbl_qna  order by qnaId desc
    ) where




     rownum <= #{pageNum} * #{amount}
) 
where rn > (#{pageNum} - 1) * #{amount}
