mapper.update:
update tbl_review 
set reviewName=#{reviewName}, reviewDescription=#{reviewDescription}, reviewPrice=#{reviewPrice}, reviewCategory=#{reviewCategory}, reviewBrand=#{reviewBrand}, reviewImageURL=#{reviewImageURL}
where reviewId=#{reviewId}

mapper.select:

select reviewId, reviewName, reviewDescription, reviewPrice, reviewCategory, reviewBrand, reviewImageURL, reviewRegdate, reviewUpdateDate from (
select /*+INDEX_DESC(tbl_review pk_review) */ rownum rn, reviewId, reviewName, reviewDescription, reviewPrice, reviewCategory, reviewBrand, reviewImageURL, reviewRegdate, reviewUpdateDate
from tbl_review 
where rownum <=  #{pageNum} * #{amount}
) where rn > (#{pageNum}-1) * #{amount}
