mapper.update:
update tbl_review 
set reviewTitle=#{reviewTitle}, reviewContent=#{reviewContent}, reviewWriter=#{reviewWriter}, reviewImageURL=#{reviewImageURL}, reviewUpdateDate=#{reviewUpdateDate}
where reviewId=#{reviewId}

mapper.select:

            select reviewId, itemId, reviewTitle, reviewContent, reviewWriter, reviewImageURL, reviewRegdate, reviewUpdateDate 
            from (
            select rownum rn, reviewId, itemId, reviewTitle, reviewContent, reviewWriter, reviewImageURL, reviewRegdate, reviewUpdateDate
            from (select * from tbl_review order by




	             reviewTitle 


	             reviewWriter 


	        	 reviewId 




        		 asc)
            where 



            rownum <=  #{pageNum} * #{amount}
            ) where rn > (#{pageNum}-1) * #{amount}


            select reviewId, itemId, reviewTitle, reviewContent, reviewWriter, reviewImageURL, reviewRegdate, reviewUpdateDate from (
            select rownum rn, reviewId, itemId, reviewTitle, reviewContent, reviewWriter, reviewImageURL, reviewRegdate, reviewUpdateDate
            from (select * from tbl_review order by




	             reviewTitle 


	             reviewWriter 


	        	 reviewId 




        		 desc)
            where 



            rownum <=  #{pageNum} * #{amount}
            ) where rn > (#{pageNum}-1) * #{amount}
